catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	if(newValue != oldValue){
		g_form.hideFieldMsg('u_slt_src_table');
		g_form.setValue('u_cb_src_eop',false);

		var instance = g_form.getValue('u_slt_src_instance');
		var basicauth = g_form.getValue('u_ref_src_basicauth');
		var rcc = g_form.getValue('u_cb_src_rcc');	
		var restuser = g_form.getValue('u_slt_src_restuser');
		var restpass = g_form.getValue('u_msk_src_restpass');

		if(basicauth == "" || rcc == "true"){
			basicauth = "";
		}

		var ga = new GlideAjax('orthusUtilsAjax');
		ga.addParam('sysparm_name', 'testTableField');
		ga.addParam('sysparm_rest', 'Orthus REST Calls');
		ga.addParam('sysparm_method', 'Get Table');
		ga.addParam('sysparm_instance', instance);
		ga.addParam('sysparm_table', newValue);
		ga.addParam('sysparm_user', restuser);
		ga.addParam('sysparm_pass', restpass);
		ga.addParam('sysparm_profile', basicauth);

		ga.getXML(getResponse);
	}

	function getResponse(response) {
		var answer = response.responseXML.documentElement.getAttribute("answer");
		if(answer){
			answers = answer.split(',');
			g_form.showFieldMsg('u_slt_src_table',answers[1],answers[0]);
			if(answers[0] == "error"){
				g_form.setValue('u_cb_src_eop',true);
			}
		}
	}
}
